import React, { useState } from 'react';

function Calculator() {
  const [displayValue, setDisplayValue] = useState('');

  // Function to add a new value to the display
  function newValue(value) {
    if (displayValue === 'Syntax Error') {
      setDisplayValue('');
    }
    if (value === '.') {
      // Check if the display already contains a decimal point
      if (displayValue.includes('.')) {
        // If so, do not add another one
        return;
      }
    }
    setDisplayValue(displayValue + value);
  }

  // Function to clear the input field
  function clearInput() {
    setDisplayValue('');
  }

  // Function to remove the last character from the input field
  function remove() {
    setDisplayValue(displayValue.slice(0, -1));
  }

  // Function to evaluate the input and display the result
  function calculateResult() {
    try {
      const result = eval(displayValue);
      setDisplayValue(result.toString());
    } catch (error) {
      setDisplayValue('Syntax Error');
    }
  }

  // Function to clear the input field and reset the calculator
  function allClear() {
    setDisplayValue('');
  }

  // Handle keydown events
  function handleKeyDown(event) {
    const key = event.key;
    if (key >= 0 && key <= 9) {
      newValue(key);
    } else if (key === '+' || key === '-' || key === '*' || key === '/') {
      newValue(key);
    } else if (key === '.') {
      newValue('.');
    } else if (key === 'Backspace') {
      remove();
    } else if (key === 'Enter') {
      calculateResult();
    } else if (key === 'Escape') {
      allClear();
    }
  }

  return (
    <div onKeyDown={handleKeyDown} tabIndex="0">
      <div id="display">{displayValue}</div>
      <button onClick={() => clearInput()}>C</button>
      <button onClick={() => remove()}>CE</button>
      <button onClick={() => newValue('+')}>+</button>
      <button onClick={() => newValue('-')}>-</button>
      <button onClick={() => newValue('*')}>*</button>
      <button onClick={() => newValue('/')}>/</button>
      <button onClick={() => newValue('7')}>7</button>
      <button onClick={() => newValue('8')}>8</button>
      <button onClick={() => newValue('9')}>9</button>
      <button onClick={() => newValue('4')}>4</button>
      <button onClick={() => newValue('5')}>5</button>
      <button onClick={() => newValue('6')}>6</button>
      <button onClick={() => newValue('1')}>1</button>
      <button onClick={() => newValue('2')}>2</button>
      <button onClick={() => newValue('3')}>3</button>
      <button onClick={() => newValue('0')}>0</button>
      <button onClick={() => newValue('.')}>.</button>
      <button onClick={() => calculateResult()}>=</button>
    </div>
  );
}

export default Calculator;
